import React, { useState } from 'react';

import { useNavigation } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, ScrollView } from 'react-native';

const BombaSubmersa = () => {
  const [niveldinamico, setniveldinamico] = useState('');
  const [alturarecalque, setalturarecalque] = useState('');
  const[comprimentototal, setcomprimentototal]= useState('');
  const[perdacarga, setperdacarga]= useState('');
 
  const calcularInversor = () => {
    // code to calculate inversor
  };
  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.titulo}>Calculo de Altura Monumétrica Total (AMT)Bomba Submersa MCA:
Instruções de cada termo</Text>

      <Text style={styles.titulo1}>Digite o Nivel Dinâmico:</Text>
      <TextInput
        style={styles.input}
        value={niveldinamico}
        onChange={setniveldinamico}

        placeholder="metros"
        keyboardType="numeric"
      />
      <Text style={styles.titulo2}>Digite a Altura de Recalque:</Text>
      <TextInput
        style={styles.input}
        value={alturarecalque}
        onChangeText={setalturarecalque}
        placeholder="metros"
        keyboardType="numeric"
      />
      <Text style={styles.titulo3}>Digite o Comprimento total da tubulação:</Text>
      <TextInput
        style={styles.input}
        value={comprimentototal}
        onChangeText={setcomprimentototal}
        placeholder="metros"
        keyboardType="numeric"
      />

      <Text style={styles.titulo4}>Digite a Perda de Carga: Baixar Tabela</Text>
      <TextInput
        style={styles.input}
        value={perdacarga}
        onChangeText={setperdacarga}
        placeholder="porcentagem"
        keyboardType="numeric"
      />
      <TouchableOpacity style={styles.button} onPress={calcularInversor}>
        <Text style={styles.buttonText}>Calcular</Text>
      </TouchableOpacity>
      
    </ScrollView>
  );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 16,
      },
  titulo: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 24,
  },
  titulo1:{
    alignSelf: 'flex-start',
    justifyContent:'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  titulo2:{
    alignSelf: 'flex-start',
    justifyContent:'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  titulo3:{
    alignSelf: 'flex-start',
    justifyContent:'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  titulo4:{
    alignSelf: 'flex-start',
    justifyContent:'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  input: {
    width: '100%',
    height: 50,
    borderWidth: 1,
    borderColor: '#CCC',
    borderRadius: 8,
    marginBottom: 16,
    paddingHorizontal: 16,
    fontSize: 18,
  },
  resultado: {
    alignSelf: 'flex-start',
    justifyContent:'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  button: {
    width: '100%',
    height: 70,
    backgroundColor: 'red',
    borderRadius: 8,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonText: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#FFF',
  },
});

export default BombaSubmersa;
