import React from 'react';
import { createStackNavigator } from '@react-navigation/native-stack';
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import { View, Text, ScrollView, StyleSheet, TouchableOpacity } from 'react-native';

const CalculoFinal = () => {
  const navigation = useNavigation();
  
  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.Text3}>O resultado do seu <Text style={{ color: 'red' }}>Sistema é:</Text></Text>
      <Text style={styles.Text2}>A Potência total da usina fotovoltaica é :</Text>
      <Text style={styles.Text1}>Tensão de Trabalho do Controlador referente ao arranjo das séries de cada bateria:</Text>
      <Text style={styles.Text2}>Carregamento em 12VDC:</Text>
      <Text style={styles.Text2}>Carregamento em 24VDC:</Text>
      <Text style={styles.Text2}>Carregamento em 48VDC:</Text>
      <Text style={styles.Text1}>Arranjo de bateria:</Text>
      <Text style={styles.Text2}>Arranjo em 12VDC:</Text>
      <Text style={styles.Text2}>Arranjo em 24VDC:</Text>
      <Text style={styles.Text2}>Arranjo em 48VDC:</Text>
      <TouchableOpacity style={[styles.button, { margin: 10 }]} onPress={() => navigation.navigate('Primeira')}>
        <Text style={styles.buttonText}>Reiniciar</Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
    container: {
        flexGrow: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 16,
    },
     Text1:{
        alignSelf: 'flex-start',
        justifyContent:'space-between',
        fontSize: 25,
        fontWeight:'bold',
        marginBottom: 10,
    },

    Text3:{
        alignSelf: 'flex-start',
        justifyContent:'space-between',
        fontSize: 30,
        fontWeight:'bold',
        marginBottom: 10,
    },
    Text2:{
        alignSelf: 'flex-start',
        justifyContent:'space-between',
        fontSize: 24,
       
        marginBottom: 10,
    },
    button: {
        width: '100%',
        height: 70,
        backgroundColor: 'red',
        borderRadius: 8,
        justifyContent: 'center',
        alignItems: 'center',
      },
      buttonText: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#FFF',
      },
});

export default CalculoFinal;
