import React, { useState } from 'react';
import {View,Text,TextInput,TouchableOpacity,StyleSheet,ScrollView,} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { NavigationContainer } from '@react-navigation/native';
import { consumoTotal } from './utils';

const CalculoDimensionamento = () => {
  const [item, setitem] = useState('');
  const [quantidadedias, setquantidadedias] = useState('');
  const [profundidade, setprofundidade] = useState('');
  const [eficiencia, seteficiencia] = useState('');
  const[resultadodi,setresultadodi]=useState('');

  const handlcalculodimensionamento= () => {

    const result = consumoTotal;
    setresultadodi(<Text style ={{color: 'red'}}>{result}</Text>)

   
  };
  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>
      Sistemas de reserva/integral de energia residêncial/industrial (Onda pura): Dimensionamento da Bateria:
      </Text>
      <Text style={styles.texto2}>Digite a quantidade de dias de autonomia: </Text>
      <TextInput
        style={styles.input}
        placeholder=""
        value={quantidadedias}
        onChangeText={setquantidadedias}
      />
      <Text style={styles.texto3}>Digite a Profundidade de Descarga (%):</Text>
      <TextInput
        style={styles.input}
        keyboardType="numeric"
        placeholder=""
        value={profundidade}
        onChangeText={setprofundidade}
      />
      <Text style={styles.texto4}>Digite a Eficiência (%):</Text>
      <TextInput
        style={styles.input}
        keyboardType="numeric"
        placeholder=""
        value={eficiencia}
        onChangeText={seteficiencia}
      />
      <TouchableOpacity style={styles.button} onPress={handlcalculodimensionamento}>
        <Text style={styles.buttonText}>Calcular</Text>
      </TouchableOpacity>
      <TouchableOpacity style={[styles.button5, { marginTop: 10 }]}>
    <Text style={styles.buttonText1}>Avançar</Text>
  </TouchableOpacity>
  {resultadodi? <Text style={styles.resultado}>Altura Monométrica Total: {resultadodi}</Text> : null}
      
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  texto1: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  resultado: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  texto2: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  texto3: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  texto4: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  texto5: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
  texto6: {
    alignSelf: 'flex-start',
    justifyContent: 'space-between',
    fontSize: 20,
    marginBottom: 10,
  },
    title: {
      alignSelf: 'flex-start',
      justifyContent:'space-between',
      fontSize: 30,
      fontWeight: 'bold',
      marginBottom: 24,
    },
    
    input: {
      width: '100%',
      height: 40,
      borderWidth: 1,
      borderColor: '#ccc',
      borderRadius: 8,
      paddingHorizontal: 16,
      marginBottom: 16,
    },
    button: {
      width: '100%',
      height: 70,
      backgroundColor: 'red',
      borderRadius: 8,
      justifyContent: 'center',
      alignItems: 'center',
    },
    buttonText: {
      fontSize: 20,
      fontWeight: 'bold',
      color: '#FFF',
    },
    button1: {
        width: '100%',
        height: 70,
        backgroundColor: 'red',
        borderRadius: 8,
        justifyContent: 'center',
        alignItems: 'center',
      },
      buttonText1: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#FFF',
      },
      button5: {
        width: '100%',
        height: 70,
        backgroundColor: 'red',
        borderRadius: 8,
        justifyContent: 'center',
        alignItems: 'center',
      },
  });
  
  export default CalculoDimensionamento;
